import React from 'react'

const FAQ = ({faq, index, toggleFAQ}) => {

    const addAnswer = () => {
        faq.answer.push("trying")
    }

    return (
        <div 
        className={"faq " + (faq.open ? 'open' : '')}
        key={index}
        onClick={() => toggleFAQ(index)}
        >
            <div className="faq-question">
                {faq.question}
            </div>
            <div className="faq-answer">
                {faq.answer.map(n=> {
                    return <li>{[n]}</li> + <br></br>
                })}

            </div>
            <input type="text" className="inputQuestion"></input>
            <button onClick={() => addAnswer} className="faq-addAnswer">Add Answer</button>

        </div>
    )
}

export default FAQ
