import React from 'react'

function FAQ ({faq, index, toggleFAQ}) {

    let answerlist = faq.answer.map(n => <li>{n}</li>)
    return (
        <div 
        className={"faq " + (faq.open ? 'open' : '')}
        key={index}
        onClick={() => toggleFAQ(index)}
        >
            <div className="faq-question">
                {faq.question}
            </div>
            <div className="faq-answer">
                {answerlist}
            </div>
        </div>
    )
}

export default FAQ
