import React from 'react'

const FAQ = ({faq, index, toggleFAQ}) => {

    const addAnswer = () => {


        const userInput = (event) => {
            faq.open = false;
            const userin = event.target.value;
            return userin
        }

        faq.answer.push(userInput);
        faq.open = false;
    }

    // const userInput = (event) => {
    //     faq.open = false;
    //     const userin = event.target.value;
    //     return userin
    // }


    return (
        <div 
        className={"faq " + (faq.open ? 'open' : '')}
        key={index}
        onClick={() => toggleFAQ(index)}
        >
            <div className="faq-question">
                {faq.question}
            </div>
            <div className="faq-answer">
                {faq.answer.map(n=> {

                    return <div> <li>{[n]}</li> <br></br>
                    </div>
                })}
            </div>
            <input type="text" className="inputQuestion" onClick={(event) => userInput(event)}></input>
            <button onClick={() => addAnswer()} className="faq-addAnswer" >Add Answer</button>

        </div>
    )
}

export default FAQ
