import React from 'react'

const FAQ = ({faq, index, toggleFAQ}) => {

    return (
        <div 
        className={"faq " + (faq.open ? 'open' : '')}
        key={index}
        onClick={() => toggleFAQ(index)}
        >
            <div className="faq-question">
                {faq.question}
            </div>
            <div className="faq-answer">
                {faq.answer.map(n=> {
                    return <li>{[n]}</li>
                })}

            </div>

            <div>
            <button>click me</button>

            </div>
        </div>
    )
}

export default FAQ
